mapper.select:
SELECT 
FROM BPM_FORM_TABLE 
WHERE MAINTABLEID = #{mainTableId}

SELECT 
FROM BPM_FORM_TABLE 
WHERE ISMAIN = 1 AND  ISPUBLISHED=1
 AND tableName  LIKE #{tableName} ESCAPE '|' 
 AND tableDesc  LIKE #{tableDesc} ESCAPE '|' 
 AND mainTableId  =#{mainTableId} 
 AND isExternal  =#{isExternal} 
 AND dsAlias  =#{dsAlias} 
 AND genByForm  =#{genByForm}

SELECT 
FROM BPM_FORM_TABLE 
WHERE dsAlias=#{dsAlias} and tableName=#{tableName}

SELECT 
FROM BPM_FORM_TABLE 
WHERE  LOWER(tableName)=#{tableName}

SELECT 
FROM BPM_FORM_TABLE 
WHERE  LOWER(tableName)=#{tableName}

SELECT 
FROM BPM_FORM_TABLE 
WHERE tableName=#{tableName}

SELECT 
FROM BPM_FORM_TABLE 
WHERE LOWER(tableName)=#{tableName}

SELECT 
FROM BPM_FORM_TABLE 
WHERE tableName=#{tableName}

select distinct a.tableId,a.tableName,a.isMain,a.tableDesc,a.mainTableId,a.isPublished, a.publishedBy,a.publishTime,a.isExternal,a.dsAlias,a.dsName,a.relation,a.keyType,a.keyValue,a.pkField,CASE WHEN b.tableId IS NULL THEN 0 ELSE 1 END hasForm ,genByForm from bpm_form_table a left join bpm_form_def b on a.tableid=b.tableid
order by ${orderField} ${orderSeq}
order by a.tableId desc
